mapper.insert:
insert into investor_workspace.t_stocks_prices (sp_stck_fk, sp_cur_fk, sp_time_set, sp_price)
values (#{sp.stockId}, #{sp.currencyId}, #{sp.settingTime}, #{sp.price});
commit;

mapper.update:
